package day03_Scanner;

public class C01_Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Soru 1:Farkli 3 data turunde variable olusturun ve bunlari yazdirin
		
				String ismim="YILMAZ";
				int sayi1=36;		
		        double ortlama=2.85;
		        
		        System.out.println(ismim);
		        System.out.println(sayi1);
		        System.out.println(ortlama);
		        
		        //Soru 2:Ýsim ve soyisim icin iki variable olusturun ve bunlari
		        String isim="YILMAZ";
		        String soyisim="TARHAN";
		        
		        System.out.println("Ýsminiz:"+ isim);
		        System.out.println("Soyisminiz:"+ soyisim);
		        
		        //Soru 3=Iki farkli tamsayi data turunde 2 variable olusturun bunlarin toplamini yazdirin
		        
		        int say1=2000;
		        short say2=21;
		        
		        System.out.println(say1+say2);
		        
		        
		        
		        // Soru 4=Bir tamsayi ve bir ondalikli variable olusturun ve bunlarin toplamini yazdirin
		        int a=25;
		        double b=2.89;
		        
		        System.out.println(a+b);
		        
		        // Soru 5=char data turunde bir variable olusturun ve yazdirin
		        char c='A';
		        System.out.println(c);
		        
		        // Soru 6=Bir tamsayi, bir de char degisken olusturun ve bunlarin toplamini yazdirin.
		        
		        int x=1932;
		        char y='Y';
		        
		        System.out.println(x+y);
		        
		        

	}

}
